import React from "react";
import Header from "./Header";
import Footer from "./Footer";

interface Doctor {
  name: string;
  image: string;
  specialization: string;
  bio: string;
  therapies: string[]; // List of therapies the doctor performs
}

const doctors: Doctor[] = [
  {
    name: "Dr. HASBULLA",
    image:
      "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ0rD-f8o0duzOnL5HkFr3owkJE2YHTqtUIYA&s",
    specialization: "Stem Cell Therapy Specialist",
    bio: "Dr. HASBULLA has over 15 years of experience in regenerative medicine, specializing in stem cell therapy for degenerative diseases and chronic pain.",
    therapies: ["Stem Cell Therapy", "Regenerative Medicine"],
  },
  {
    name: "Dr. John Doe",
    image:
      "data:image/jpeg;base64,",
    specialization: "Gene Therapy Expert",
    bio: "With a PhD in molecular biology, Dr. Doe focuses on gene therapy for genetic disorders, offering cutting-edge treatments to patients.",
    therapies: ["Gene Therapy", "Personalized Medicine"],
  },
  {
    name: "Dr. Emily Clark",
    image:
      "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRQJZ7Y5xqtFhk6Gk_2sswoEmJ4ikz0D7WMqw&s",
    specialization: "Immunotherapy Specialist",
    bio: "Dr. Clark is a renowned oncologist who integrates immunotherapy into cancer treatments, helping patients boost their immune responses against tumors.",
    therapies: ["Immunotherapy", "Monoclonal Antibodies"],
  },
  {
    name: "Dr. Michael Lee",
    image:
      "data:image/jpeg;base64,",
    specialization: "Regenerative Medicine Expert",
    bio: "Dr. Lee has pioneered various regenerative medicine techniques, helping patients recover from injuries and manage chronic conditions through cellular therapies.",
    therapies: ["Regenerative Medicine", "Stem Cell Therapy"],
  },
  {
    name: "Dr. Laura Adams",
    image:
      "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQE-n-Eb5PPHCiRyGR1vpCKxiucX1RsoZ0gEA&s",
    specialization: "Monoclonal Antibodies Specialist",
    bio: "Dr. Adams focuses on targeted therapies using monoclonal antibodies for treating autoimmune diseases and infections.",
    therapies: ["Monoclonal Antibodies", "Immunotherapy"],
  },
  {
    name: "Dr. Robert Green",
    image:
      "data:image/jpeg;base64,",
    specialization: "Personalized Medicine Expert",
    bio: "Dr. Green specializes in personalized medicine, tailoring treatments to the individual’s genetic and lifestyle factors for more effective outcomes.",
    therapies: ["Personalized Medicine", "Gene Therapy"],
  },
];

const Doctors: React.FC = () => {
  return (
    <div className="bg-white min-h-screen flex flex-col mt-24 ">
      <Header />
      <main className="flex-1 p-6 md:p-10 bg-gradient-to-r from-gray-50 to-gray-100 pb-16">
        <h2 className="text-4xl font-bold mb-12 text-center text-gray-900">
          Meet Our Expert Doctors
        </h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
          {doctors.map((doctor) => (
            <div
              key={doctor.name}
              className="bg-white shadow-lg rounded-lg overflow-hidden"
            >
              <img
                src={doctor.image}
                alt={doctor.name}
                className="w-full h-64 object-cover"
              />
              <div className="p-6">
                <h3 className="text-2xl font-semibold mb-2 text-gray-900">
                  {doctor.name}
                </h3>
                <p className="text-gray-700 mb-2 italic">
                  {doctor.specialization}
                </p>
                <p className="text-gray-600 mb-4">{doctor.bio}</p>
                <h4 className="text-lg font-semibold text-gray-800 mb-2">
                  Therapies:
                </h4>
                <ul className="list-disc list-inside text-gray-700">
                  {doctor.therapies.map((therapy) => (
                    <li key={therapy}>{therapy}</li>
                  ))}
                </ul>
              </div>
            </div>
          ))}
        </div>
      </main>
      <Footer />
    </div>
  );
};

export default Doctors;
